cxt.lineTo(150, 50);
cxt.lineTo(150, 100);
cxt.lineTo(50, 100);
cxt.stroke();

//画矩形
text('直接画矩形',140,200)
cxt.moveTo(120, 180);
cxt.lineTo(120,300); 
cxt.lineTo(200, 300);
cxt.lineTo(200, 180);
cxt.lineTo(120, 180);
cxt.stroke();

